/// <summary>  
/// formating the data that will be sent.  
/// </summary>  
/// <param name="data">the data that will be sent.</param>  
/// <param name="dataLength">the length of data so that the server can check if the data transferred successfully</param>  
/// <remarks>"dataLength" is only used in the header</remarks>  
/// <param name="spiltedData">formatting and sending data in a different way if they are splited</param>  
/// <param name="firstIndex">check if we are sending the first index of the splited data and we will put the header</param>  
/// <param name="lastIndex">check if we are sending the last index of the splited data and we will put the end of packet</param>  
private void Send(int parts, string data, int dataLength, bool spiltedData, bool firstIndex, bool lastIndex)  
{  
 string formedData = String.Empty;  
 if (firstIndex)  
 formedData += String.Format("

<{0}|{1}>", dataLength, parts);  
 if (!spiltedData)  
 {  
 formedData = data + "

";  
 }  
 else  
 {  
 formedData += data;  
 if (lastIndex)  
 formedData += data + "

";  
 }  
 Send(formedData);  
}
