How can i use this packet protocol when send file data over ssl stream.Please help. I am using this code to send data to server from client  
 private Boolean SendTCPPackets(String filePath)  
 {

 Boolean status = false;

 TcpClient client = null;  
 SslStream sslStream = null;  
 String returndata = String.Empty;  
 String reciveddata = String.Empty;

 client = new TcpClient(serverName, serverPort);

 // Create an SSL stream that will close the client's stream.  
 sslStream = new SslStream(  
 client.GetStream(),  
 false,  
 new RemoteCertificateValidationCallback(ValidateServerCertificate),  
 null  
 );  
 try  
 {

 sslStream.AuthenticateAsClient(serverName);

 String fileName = Path.GetFileName(filePath);  
 Byte[] fileNameByte = Encoding.UTF8.GetBytes(fileName);  
 Byte[] fileNameLen = BitConverter.GetBytes(fileNameByte.Length);

 String filetext = File.ReadAllText(filePath).Trim();

 Byte[] fileData = Encoding.UTF8.GetBytes(filetext);  
 Byte[] fileDatalenByte = BitConverter.GetBytes(fileData.Length);

 Byte[] clientData = new Byte[4 + fileDatalenByte.Length + fileNameByte.Length + fileData.Length];

 fileNameLen.CopyTo(clientData, 0);  
 fileDatalenByte.CopyTo(clientData, 4);  
 fileNameByte.CopyTo(clientData, 4 + fileDatalenByte.Length);  
 fileData.CopyTo(clientData, 4 + fileDatalenByte.Length + fileNameByte.Length);

 client = new TcpClient(serverName, serverPort);

 client.Client.SendTimeout = 5000;  
 client.Client.ReceiveTimeout = 5000;  
 Byte[] messsage = clientData;  
 // Send message to the server.  
 sslStream.Write(messsage);  
 sslStream.Flush();

 String serverMessage = ReadMessage(sslStream);  
 serverMessage = serverMessage.Substring(0, serverMessage.Length - 5);  
 if (serverMessage.ToLower() == "true")  
 {  
 status = true;

 }  
 else  
 {  
 status = false;  
 }  
 //status = false;  
 // Read message from the server.

 }  
 catch (AuthenticationException e)  
 {  
 Console.WriteLine("Exception: {0}", e.Message);  
 if (e.InnerException != null)  
 {  
 Console.WriteLine("Inner exception: {0}", e.InnerException.Message);  
 }  
 Console.WriteLine("Authentication failed - closing the connection.");  
 //client.Close();  
 return false;  
 }

 finally  
 {  
 //client.GetStream().Close();  
 //sslStream.Close();  
 client.Close();  
 }

 return status;  
 }
