       public async Task ParentMethodAsync() {


           await ReadFileAsyncMethod();  // Will suspend the current thread and execute asynchronously on same thread.

          ReadFileAsyncMethod(); // Will not suspend the current thread and execute synchronously.    

      }

     public async Task ReadFileAsyncMethodAsync(){

            // Some code written here.

     }

